import React, {useState, useRef} from 'react';
import {Map} from './Map';
import {Calender} from './Calender';

import axios from 'axios';
import './css/App.css';

const App = () => {
  // DOMに現れるものをuseState，表れないものをuseRefにする（レンダリングするかしないか）
  const [TimeDates, setTimeDates] = useState([]);

  const GetTimeList = async (selectedDate) => {
    console.log(selectedDate);
    const URL = `http://ezaki-lab.littlestar.jp/nict/api/get_start_data.php?date=${selectedDate}`;
    const response = await axios.post(URL);
    console.log(response.data);
    setTimeDates(response.data);
  }

  return (
    <div className="App">
      <div id="date_div" className="calendar_container">
      <h1>日付選択</h1>
        <Calender GetTimeList={GetTimeList} TimeDates={TimeDates} setTimeDates={setTimeDates}/>
      </div>
      <Map/>
    </div>
  );
}

export default App;
