import React, {useState, useRef} from 'react';
import {Map} from './Map';
import {Calender} from './Calender';

import axios from 'axios';
import './css/App.css';

const App = () => {
  // DOMに現れるものをuseState，表れないものをuseRefにする（レンダリングするかしないか）
  const d = new Date();
  const [TimeDates, setTimeDates] = useState([]);
  const [Dates, setDates] = useState([]);
  const startDate = useRef(`${d.getFullYear()}-${d.getMonth()+1}-1`);
  const endDate = useRef(`${d.getFullYear()}-${d.getMonth()+2}-1`);

  const GetTimeList = async (selectedDate) => {
    console.log(selectedDate);
    const URL = `http://ezaki-lab.littlestar.jp/nict/api/get_start_data.php?date=${selectedDate}`;
    const response = await axios.post(URL);
    console.log(response.data);
    setTimeDates(response.data);
  }

  const GetEvent = async () => { //非同期関数
    // APIからデータを取得
    const URL = `https://ezaki-lab.littlestar.jp/nict/api/get_date.php?start_date=${startDate.current}&end_date=${endDate.current}`;

    try {
      const response = await axios.post(URL);
      let event_date = [];
      for(let i = 0; i < response.data.length; i++) {
        event_date.push({title : "", date : `${response.data[i]['date']}`});
      }
      setDates(event_date);
    }
    catch(error) {
      switch (error.response.status) {
        case 400:
          console.log('認証エラーです');
          break;
        case 401:
          console.log('認証エラーです');
          break;
        case 404:
          console.log('URL先がないです');
          break;
        default:
          console.log(error);
      }
    };
  }

  return (
    <div className="App">
      <Calender GetTimeList={GetTimeList} TimeDates={TimeDates}/>
      <Map/>
    </div>
  );
}

export default App;
