import { LoadScript, GoogleMap, Polyline } from "@react-google-maps/api";
import React, { useState, useEffect } from "react";

const MapWithPolyline = ({Positions, centerPositions}) => {
  useEffect(() => {
    const polylinePath = Positions.map(item => ({ lat: item.lat, lng: item.lng }));
    setPolylinePath(polylinePath);
  }, [Positions]);

  return (
    <LoadScript googleMapsApiKey={"AIzaSyCjpcBI57XAEzvIqzNBEj4eIpVzRaRe93U"}>
      <GoogleMap
        id="myMap"
        mapContainerStyle={{height: "100vh", width: "80vw"}}
        zoom={22}
        center={centerPositions}
        mapOptions={{
          disabledDefaultUI: true, 
          zoomControl: true,
          streetViewControl: false,
          mapTypeControl: false,
        }}
        mapTypeId="satellite" // 地図タイプを航空写真に設定
      >
        {
          Positions.length > 0 ? (
            <Polyline 
              path={Positions}
              options={{
                strokeColor : "#FF0000",
                strokeOpacity : 1,
                strokeWeight : 1
              }}
            />
          )
          : <></>
        }
      </GoogleMap>
    </LoadScript>
  )
}

export const Map = ({Positions, centerPositions}) => {
  // console.log(Positions);
  return (
    <div>
      <MapWithPolyline Positions={Positions} centerPositions={centerPositions}/>
    </div>
  )
};
