// 参考 https://qiita.com/FumioNonaka/items/1810f7e211638988af62
import React from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid"; // pluginは、あとから
import allLocales from '@fullcalendar/core/locales-all';

const thisMonth = () => {
  const today = new Date();
  return `${today.getFullYear()}-${String(today.getMonth() + 1).padStart(
    2,
    "0"
  )}`;
};

export const Date_view = () => {
  return(
    <div id="date_div">
      <h1>日付選択</h1>
      <FullCalendar 
        plugins={[dayGridPlugin]} 
        initialView="dayGridMonth" 
        locales={allLocales}
        locale="ja"
        // events={[
        //   { title: "event 1", date: `${thisMonth()}-01` },
        //   { title: "event 2", date: `${thisMonth()}-02` },
        // ]}
      />
    </div>
  )
}
