import React, {useEffect} from "react"

export const TimeList = ({TimeDates, GetPositions, selectedTime, setSelectedTime}) => {
  // 時間選択
  const SelectRadio = (e) => {
    console.log(e.target.value);
    // setSelectedTime(e.target.value);
    GetPositions(e.target.value);
  }

  // レンダリング時に最初の時間を選択状態に設定
  useEffect(() => {
    if (TimeDates.length > 0) {
      setSelectedTime(TimeDates[0].datetime); // 最初の時間を選択
      GetPositions(TimeDates[0].datetime); // 対応する緯度経度を取得
    }
  }, [TimeDates, setSelectedTime]);

  return (
    <div id="TimeList">
      {TimeDates.length > 0 ? (
          TimeDates.map((time, index) => (
            <label key={index}>
              <input
                type="radio" 
                name='time' 
                value={time.datetime} 
                checked={selectedTime === time.datetime} 
                onChange={SelectRadio}
              />
              {time.datetime}から<br/>
            </label>))
        ) : (
          <p>データがありません</p>
        )
      }
    </div>
  )
}
