// 参考 https://qiita.com/FumioNonaka/items/1810f7e211638988af62
import React, {useCallback, useState} from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid"; // pluginは、あとから
import allLocales from '@fullcalendar/core/locales-all';
import interactionPlugin, { DateClickArg } from "@fullcalendar/interaction";
import axios from 'axios';
axios.defaults.baseURL = 'http://localhost:3000';
axios.defaults.headers.post['Content-Type'] = 'application/json;charset=utf-8';

export const DateView = () => {
  // 日付をクリックした時
  const handleDateClick = useCallback((arg) => {
    alert(arg.dateStr);
  }, []);

  // 
  const handleDatesClick = (arg) => {
    console.log(arg);
  }

  const [posts, setPosts] = useState([]);

  // APIからデータを取得
  const DateURL = "https://ezaki-lab.littlestar.jp/nict/api/get_date.php?year=2023&month=9";
  // const data = new URLSearchParams();
  // data.append('year', '2023'); // 任意の年
  // data.append('month', '9');   // 任意の月

  axios.post(DateURL)
  .then(function (response) {
    // console.log(response.data);
    setPosts(response.data);
  })
  .catch(function (error) {
    // console.log(error);
    switch (error.response.status) {
      case 400:
        console.log('認証エラーです');
        break;
      case 401:
        console.log('認証エラーです');
        break;
      case 404:
        console.log('URL先がないです');
        break;
      default:
        console.log(error);
    }
  });

  let event_date = [];
  for(let i = 0; i < posts.length; i++) {
    event_date.push({title : "データあり", date : `${posts[i]['date']}`});
  }


  return(
    <div id="date_div">
      <h1>日付選択</h1>
      <FullCalendar 
        plugins={[dayGridPlugin, interactionPlugin]} 
        initialView="dayGridMonth" //表示の種類
        locales={allLocales} //全部を日本語化
        locale="ja" //日本語表記
        // イベントを表示 forで配列作って入れ込む?
        events={event_date}
        dateClick={handleDateClick}
        datesSet={handleDatesClick}
      />
    </div>
  )
}
