import React, {useState, useEffect} from "react"

export const TimeList = ({TimeDates, GetPositions}) => {
  const [selectedTime, setSelectedTime] = useState('');

  const SelectRadio = (arg) => {
    setSelectedTime(arg.value);
    GetPositions(arg.value);
  }

  useEffect(() => {
    if(TimeDates.length > 0) {  
      SelectRadio({ value: TimeDates[0].datetime });
    }
  })

  return (
    <div id="TimeList">
      <h3>開始時時刻</h3>
      {TimeDates.length > 0 ? (
          TimeDates.map((time, index) => (
            <label key={index}>
              <input
                type="radio" 
                name='time' 
                value={time.datetime} 
                checked={selectedTime===time.datetime} 
                onChange={() => SelectRadio(time)}
              />
              {time.datetime}から<br/>
            </label>))
        ) : (
          <p>データがありません</p>
        )
      }
    </div>
  )
}
