import React, {useState} from 'react';
import {Map} from './Map';
import {Calender} from './Calender';

import axios from 'axios';
import './css/App.css';
import { TimeList } from './TimeList';

const App = () => {
  // DOMに現れるものをuseState，表れないものをuseRefにする（レンダリングするかしないか）
  const [TimeDates, setTimeDates] = useState([]); //開始時間時間
  const [Positions, setPositions] = useState([]); //緯度経度

  const AccessApi = async (URL) => {
    try {
      const response = await axios.post(URL);
      return response.data;
    } catch (error) {
      console.log(URL);
      switch (error.response.status) {
        case 400:
          console.log('認証エラーです');
          break;
        case 401:
          console.log('認証エラーです');
          break;
        case 404:
          console.log('URL先がないです');
          break;
        default:
          console.log(error);
      }
      throw error;
    }
  };

  const GetTimeList = async (selectedDate) => {
    console.log(selectedDate);
    const URL = `http://ezaki-lab.littlestar.jp/nict/api/get_start_data.php?date=${selectedDate}`;
    const res = await AccessApi(URL);
    console.log(res);
    setTimeDates(res);
  }

  const GetPositions = async (checkedTime) => {
    const checkedTimeIndex = TimeDates.indexOf(checkedTime);
    if(checkedTimeIndex !== -1) {
      let URL = '';
      if(checkedTimeIndex !== TimeDates.length - 1) {
        const startDate = TimeDates[checkedTimeIndex];
        const endDate = TimeDates[checkedTimeIndex + 1];
        URL = `http://ezaki-lab.littlestar.jp/nict/api/get_date.php?start_date=${startDate}&end_date=${endDate}`;
      }
      else{
        const startDate = TimeDates[checkedTimeIndex];
        URL = `http://ezaki-lab.littlestar.jp/nict/api/get_date.php?start_date=${startDate}`;
      }

      if(URL !== ''){
        const response = await axios.post(URL);
        console.log(response.data);
        setPositions(response.data);      
      }
    }
  }

  return (
    <div className="App">
      <div id="date_div" className="calendar_container">
        <h1>日付選択</h1>
        <Calender GetTimeList={GetTimeList} setTimeDates={setTimeDates}/>
        <TimeList TimeDates={TimeDates} GetPositions={GetPositions}/>
      </div>
      <Map/>
    </div>
  );
}

export default App;
