import React, {useState, useEffect} from 'react';
import {Map} from './Map';
import {Calender} from './Calender';
import { TimeList } from './TimeList';
import axios from 'axios';
import './css/App.css';

const App = () => {
  const d = new Date();
  const [selectDate, setSelectDate] = useState(`${d.getFullYear()}-${d.getMonth()+1}-${d.getDate()}`);
  const [TimeDates, setTimeDates] = useState([]);

  const GetTimeList = async (selectedDate) => {
    console.log(selectedDate);
    setSelectDate(selectedDate);
    const URL = `http://ezaki-lab.littlestar.jp/nict/api/get_start_data.php?date=${selectedDate}`;
    const response = await axios.post(URL);

    console.log(response.data);
  }

  // useEffect(() => {
  //   console.log(selectDate);
  //   GetTimeList();
  //   // eslint-disable-next-line react-hooks/exhaustive-deps
  // }, [selectDate]);

  return (
    <div className="App">
      <Calender GetTimeList={GetTimeList}/>
      <TimeList TimeDates={TimeDates}/>
      <Map/>
    </div>
  );
}

export default App;
