// 参考 https://qiita.com/FumioNonaka/items/1810f7e211638988af62
import React, {useCallback, useRef, useEffect} from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid"; // pluginは、あとから
import allLocales from '@fullcalendar/core/locales-all';
import interactionPlugin from "@fullcalendar/interaction";
import axios from 'axios';
import "./css/Calender.css"
import { TimeList } from './TimeList';
// axios.defaults.baseURL = 'http://localhost:3000';
// axios.defaults.headers.post['Content-Type'] = 'application/json;charset=utf-8';

export const Calender = ({GetTimeList, TimeDates, GetEvent, Dates}) => {
  // const events = useRef([]);
  const calendarRef = useRef(null);

  // 日付をクリックした時
  const HandleDateClick = useCallback((arg) => {
    const selectDate = arg.dateStr;
    const events_date = Dates.map((item) => {
      return item.date;
    });
    if(events_date.includes(selectDate)) {
      GetTimeList(arg.dateStr);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // 前後の月に移動した時 https://zenn.dev/takky94/articles/7d10cfc95c77de
  const HandleDatesClick = (arg) => {
    GetEvent(arg);

  }

  return(
    <div id="date_div" className="calendar_container">
      <h1>日付選択</h1>
      <FullCalendar 
        ref={calendarRef}
        plugins={[dayGridPlugin, interactionPlugin]} 
        initialView="dayGridMonth" //表示の種類
        locales={allLocales} //全部を日本語化
        locale="ja" //日本語表記
        // イベントを表示 forで配列作って入れ込む?
        events={Dates}
        dateClick={HandleDateClick}
        datesSet={HandleDatesClick}
        contentHeight="auto" // 高さを自動調整
      />
      <TimeList TimeDates={TimeDates}/>
    </div>
  )
}
