// 参考 https://qiita.com/FumioNonaka/items/1810f7e211638988af62
import React, {useCallback, useEffect, useState} from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid"; // pluginは、あとから
import allLocales from '@fullcalendar/core/locales-all';
import interactionPlugin, { DateClickArg } from "@fullcalendar/interaction";
import axios from 'axios';
// axios.defaults.baseURL = 'http://localhost:3000';
// axios.defaults.headers.post['Content-Type'] = 'application/json;charset=utf-8';

export const DateView = () => {
  const [posts, setPosts] = useState([]);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');

  // 日付をクリックした時
  const handleDateClick = useCallback((arg) => {
    alert(arg.dateStr);
  }, []);

  // 前後の月に移動した時 https://zenn.dev/takky94/articles/7d10cfc95c77de
  const handleDatesClick = (arg) => {
    const tempStartDate = arg.start;
    const tempEndDate = arg.end;
    // console.log(tempStartDate.getFullYear());

    setStartDate(`${tempStartDate.getFullYear()}-${tempStartDate.getMonth()+1}-${tempStartDate.getDate()}`);
    setEndDate(`${tempEndDate.getFullYear()}-${tempEndDate.getMonth()+1}-${tempEndDate.getDate()}`);
  }



  useEffect(() => {
    // APIからデータを取得
    const DateURL = `https://ezaki-lab.littlestar.jp/nict/api/get_date.php?start_date=${startDate}&end_date=${endDate}`;

    axios.post(DateURL)
    .then(function (response) {
      // console.log(response.data);
      setPosts(response.data);
    })
    .catch(function (error) {
      // console.log(error);
      switch (error.response.status) {
        case 400:
          console.log('認証エラーです');
          break;
        case 401:
          console.log('認証エラーです');
          break;
        case 404:
          console.log('URL先がないです');
          break;
        default:
          console.log(error);
      }
    });

    let event_date = [];
    // console.log(posts);
    for(let i = 0; i < posts.length; i++) {
      event_date.push({title : "データあり", date : `${posts[i]['date']}`});
    }
  })


  return(
    <div id="date_div">
      <h1>日付選択</h1>
      <FullCalendar 
        plugins={[dayGridPlugin, interactionPlugin]} 
        initialView="dayGridMonth" //表示の種類
        locales={allLocales} //全部を日本語化
        locale="ja" //日本語表記
        // イベントを表示 forで配列作って入れ込む?
        events={event_date}
        dateClick={handleDateClick}
        datesSet={handleDatesClick}
      />
    </div>
  )
}
