// 参考 https://qiita.com/FumioNonaka/items/1810f7e211638988af62
import React, {useCallback, useEffect, useState} from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid"; // pluginは、あとから
import allLocales from '@fullcalendar/core/locales-all';
import interactionPlugin from "@fullcalendar/interaction";
import axios from 'axios';
// axios.defaults.baseURL = 'http://localhost:3000';
// axios.defaults.headers.post['Content-Type'] = 'application/json;charset=utf-8';

export const DateView = () => {
  // const [posts, setPosts] = useState([]); //
  const [events, setEvents] = useState([]);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');

  // 日付をクリックした時
  const HandleDateClick = useCallback((arg) => {
    alert(arg.dateStr);
  }, []);

  // 前後の月に移動した時 https://zenn.dev/takky94/articles/7d10cfc95c77de
  const HandleDatesClick = (arg) => {
    const tempStartDate = arg.start;
    const tempEndDate = arg.end;

    setStartDate(`${tempStartDate.getFullYear()}-${tempStartDate.getMonth()+1}-${tempStartDate.getDate()}`);
    setEndDate(`${tempEndDate.getFullYear()}-${tempEndDate.getMonth()+1}-${tempEndDate.getDate()}`);
  }

  const SetEvent = async () => { //非同期関数
    // APIからデータを取得
    let DateURL = ''
    if(startDate === ''){
      // 現在の年と月を取得
      const d = new Date();
      setStartDate(`${d.getFullYear()}-${d.getMonth()+1}-1`);
      setEndDate(`${d.getFullYear()}-${d.getMonth()+2}-1`);
      DateURL = `https://ezaki-lab.littlestar.jp/nict/api/get_date.php?start_date=${d.getFullYear()}-${d.getMonth()+1}-1&end_date=${d.getFullYear()}-${d.getMonth()+2}-1`;
    }
    else{
      DateURL = `https://ezaki-lab.littlestar.jp/nict/api/get_date.php?start_date=${startDate}&end_date=${endDate}`;
    }

    try {
      const response = await axios.post(DateURL);
      // setPosts(response.data);
      let event_date = [];
      for(let i = 0; i < response.data.length; i++) {
        event_date.push({title : "データあり", date : `${response.data[i]['date']}`});
      }
      setEvents(event_date);

      // setPosts(response.data);
    }
    catch(error) {
      // console.error(error);
      switch (error.response.status) {
        case 400:
          console.log('認証エラーです');
          break;
        case 401:
          console.log('認証エラーです');
          break;
        case 404:
          console.log('URL先がないです');
          break;
        default:
          console.log(error);
      }
    };
  }

  // 第2因数の値に変化があったときに実行
  useEffect(() => {
    SetEvent();
  }, [startDate, endDate]);


  return(
    <div id="date_div">
      <h1>日付選択</h1>
      <FullCalendar 
        plugins={[dayGridPlugin, interactionPlugin]} 
        initialView="dayGridMonth" //表示の種類
        locales={allLocales} //全部を日本語化
        locale="ja" //日本語表記
        // イベントを表示 forで配列作って入れ込む?
        events={events}
        dateClick={HandleDateClick}
        datesSet={HandleDatesClick}
        contentHeight="auto" // 高さを自動調整
  contentWidth="auto" // 横幅を自動調整

      />
    </div>
  )
}
